package org.jzw.yxbs.condition;import java.util.Date;import org.jzw.yxbs.utils.DateFormatUtil;public abstract class BaseCondition {	public static Integer PAGE_INDEX = 0;	public static Integer PAGE_SIZE = 10;		protected Date beginDate;	protected Date endDate;	protected Integer pageIndex = PAGE_INDEX;	protected Integer pageSize = PAGE_SIZE;	public Date getBeginDate() {		return beginDate;	}	public void setBeginDate(Date beginDate) {		this.beginDate = beginDate;	}	public Date getEndDate() {		return endDate;	}	public void setEndDate(Date endDate) {		this.endDate = endDate;	}	public Integer getPageIndex() {		if (pageIndex == null) {			return PAGE_INDEX;		}		return pageIndex;	}	public void setPageIndex(Integer pageIndex) {		this.pageIndex = pageIndex;	}	public Integer getPageSize() {		if (pageSize == null) {			return PAGE_SIZE;		}		return pageSize;	}	public void setPageSize(Integer pageSize) {		this.pageSize = pageSize;	}		public void setDisplayBeginDate(String displayBeginDate){		if (displayBeginDate == null||displayBeginDate.trim().equalsIgnoreCase("")) {			this.beginDate = null;		} else {			this.beginDate = DateFormatUtil.StringToDate(displayBeginDate);		}	}		public String getDisplayBeginDate(){		if (beginDate==null) {			return "";		}		return DateFormatUtil.dateToString(beginDate);	}		public void setDisplayEndDate(String displayEndDate){		if (displayEndDate == null||displayEndDate.trim().equalsIgnoreCase("")) {			this.endDate = null;		} else {			Date tomorrow = DateFormatUtil.StringToDate(displayEndDate);			this.endDate = DateFormatUtil.getTomorrow(tomorrow);		}	}		public String getDisplayEndDate(){		if (endDate==null) {			return null;		}		Date yseterday = DateFormatUtil.getYesterday(endDate);		return DateFormatUtil.dateToString(yseterday);	}	public Integer getPageBeginIndex() {		pageIndex = (pageIndex == null ? 0 : pageIndex);		pageSize = (pageSize == null ? 0 : pageSize);		return pageIndex * pageSize;	}}