package org.jzw.yxbs.condition;import java.util.Date;public abstract class BaseCondition {		protected Date beginDate;	protected Date endDate;	protected Integer pageIndex;	protected Integer pageSize;		public Date getBeginDate() {		return beginDate;	}	public void setBeginDate(Date beginDate) {		this.beginDate = beginDate;	}	public Date getEndDate() {		return endDate;	}	public void setEndDate(Date endDate) {		this.endDate = endDate;	}	public Integer getPageIndex() {		return pageIndex;	}	public void setPageIndex(Integer pageIndex) {		this.pageIndex = pageIndex;	}	public Integer getPageSize() {		return pageSize;	}	public void setPageSize(Integer pageSize) {		this.pageSize = pageSize;	}}