package org.jzw.yxbs.condition;import java.util.Date;public abstract class BaseCondition {	public static Integer PAGE_INDEX = 0;	public static Integer PAGE_SIZE = 10;		protected Date beginDate;	protected Date endDate;	protected Integer pageIndex = PAGE_INDEX;	protected Integer pageSize = PAGE_SIZE;	public Date getBeginDate() {		return beginDate;	}	public void setBeginDate(Date beginDate) {		this.beginDate = beginDate;	}	public Date getEndDate() {		return endDate;	}	public void setEndDate(Date endDate) {		this.endDate = endDate;	}	public Integer getPageIndex() {		if (pageIndex == null) {			return PAGE_INDEX;		}		return pageIndex;	}	public void setPageIndex(Integer pageIndex) {		this.pageIndex = pageIndex;	}	public Integer getPageSize() {		if (pageSize == null) {			return PAGE_SIZE;		}		return pageSize;	}	public void setPageSize(Integer pageSize) {		this.pageSize = pageSize;	}	public Integer getPageBeginIndex() {		pageIndex = (pageIndex == null ? 0 : pageIndex);		pageSize = (pageSize == null ? 0 : pageSize);		return pageIndex * pageSize;	}}