package org.jzw.yxbs.condition;public class UserInfoCondition extends BaseCondition {		private String username;	private String userInfoAttrid;	private String userInfoAttrval;		public String getUsername() {		return username;	}	public void setUsername(String username) {		this.username = username;	}	public String getUserInfoAttrid() {		return userInfoAttrid;	}	public void setUserInfoAttrid(String userInfoAttrid) {		this.userInfoAttrid = userInfoAttrid;	}	public String getUserInfoAttrval() {		return userInfoAttrval;	}	public void setUserInfoAttrval(String userInfoAttrval) {		this.userInfoAttrval = userInfoAttrval;	}	}