package org.jzw.yxbs.controller;import org.apache.log4j.Logger;import org.jzw.yxbs.utils.UserInfoUtil;import org.springframework.stereotype.Controller;import org.springframework.ui.ModelMap;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.servlet.ModelAndView;@Controller@RequestMapping("/index")public class IndexController {	private static final Logger LOGGER = Logger.getLogger(IndexController.class);		@RequestMapping(value = "welcome")	public String welcome() {		LOGGER.info("welcome!");		return "welcome";	}	@RequestMapping(value = "login")	public ModelAndView login() {		LOGGER.info("loginï¼š" + UserInfoUtil.getUserName());		LOGGER.info("md5:" + UserInfoUtil.getEncodePasswd("zzd", "zzd123"));		ModelMap modelMap = new ModelMap();		modelMap.addAttribute("username", UserInfoUtil.getUserName());		return new ModelAndView("login", modelMap);	}	@RequestMapping(value = "notlogin")	public String notlogin() {		LOGGER.info("notlogin!");		return "notlogin";	}	@RequestMapping(value = "menu1")	public String menu1() {		return "menu1";	}	@RequestMapping(value = "menu2")	public String menu2() {		return "menu2";	}}