package org.jzw.yxbs.controller;import org.apache.log4j.Logger;import org.jzw.yxbs.utils.UserInfoUtil;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;@Controller@RequestMapping("/index")public class IndexController {	private static final Logger LOGGER = Logger.getLogger(IndexController.class);		@RequestMapping(value = "welcome")	public String welcome() {		LOGGER.info("welcome!");		return "index/welcome";	}	@RequestMapping(value = "login")	public String login() {		LOGGER.info("login：" + UserInfoUtil.getUserName());		return "index/login";	}	@RequestMapping(value = "notlogin")	public String notlogin() {		LOGGER.info("notlogin!");		return "index/notlogin";	}		@RequestMapping(value = "accessDenied")	public String accessDenied(){		LOGGER.info("403：" + UserInfoUtil.getUserName());		return "common/403";	}		@RequestMapping(value = "about")	public String about(){		return "index/about";	}	@RequestMapping(value = "bbs")	public String bbs() {		return "index/bbs";	}	}