package org.jzw.yxbs.controller;import java.util.ArrayList;import java.util.List;import org.jzw.yxbs.vo.ChartsVo;import org.springframework.stereotype.Controller;import org.springframework.ui.ModelMap;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.servlet.ModelAndView;@Controller@RequestMapping("/reports")public class ReportsController {	@RequestMapping(value = "main")	public ModelAndView main() {		ModelMap modelMap = new ModelMap();		List<ChartsVo> list = new ArrayList<ChartsVo>();		ChartsVo p1 = new ChartsVo();		p1.setColor("#F38630");		p1.setValue("20%");		ChartsVo p2 = new ChartsVo();		p2.setColor("#E0E4CC");		p2.setValue("24.5%");		ChartsVo p3 = new ChartsVo();		p3.setColor("#69D2E7");		p3.setValue("55.5%");		list.add(p1);		list.add(p2);		list.add(p3);		List<ChartsVo> list2 = new ArrayList<ChartsVo>();		ChartsVo p4 = new ChartsVo();		p4.setColor("#dcdcdc");		p4.setValue("进货数量");		ChartsVo p5 = new ChartsVo();		p5.setColor("#97bbcd");		p5.setValue("出货数量");		list2.add(p4);		list2.add(p5);		modelMap.addAttribute("result", list);		modelMap.addAttribute("result2", list2);		return new ModelAndView("reports/main",modelMap);	}}