package org.jzw.yxbs.controller;import org.springframework.stereotype.Controller;import org.springframework.ui.ModelMap;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.servlet.ModelAndView;@Controller@RequestMapping("/reports")public class ReportsController {	@RequestMapping(value = "main")	public ModelAndView main() {		ModelMap modelMap = new ModelMap();		return new ModelAndView("reports/main",modelMap);	}}