package org.jzw.yxbs.controller;import org.jzw.yxbs.utils.UserInfoUtil;import org.springframework.stereotype.Controller;import org.springframework.ui.ModelMap;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.servlet.ModelAndView;@Controller@RequestMapping("/userMgr")public class UserMgrController {		@RequestMapping(value = "main")	public ModelAndView main(){		ModelMap modelMap = new ModelMap();		modelMap.addAttribute("username", UserInfoUtil.getUserName());		return new ModelAndView("userMgr/main", modelMap);	}	}