package org.jzw.yxbs.controller;import javax.annotation.Resource;import org.apache.log4j.Logger;import org.jzw.yxbs.condition.UserCondition;import org.jzw.yxbs.service.UserService;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;@Controller@RequestMapping("/userMgr")public class UserMgrController {		private static final Logger LOGGER = Logger.getLogger(UserMgrController.class);		@Resource	private UserService userService;		@RequestMapping(value = "main")	public String main(){		UserCondition userCondition = new UserCondition();		userCondition.setUsername("d2");		userService.findUsers(userCondition);		return "userMgr/main";	}	}