package org.jzw.yxbs.controller;import java.util.List;import javax.annotation.Resource;import org.apache.log4j.Logger;import org.jzw.yxbs.condition.UserCondition;import org.jzw.yxbs.service.UserService;import org.jzw.yxbs.vo.UserVo;import org.springframework.stereotype.Controller;import org.springframework.ui.ModelMap;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.servlet.ModelAndView;@SuppressWarnings("unused")@Controller@RequestMapping("/userMgr")public class UserMgrController {		private static final Logger LOGGER = Logger.getLogger(UserMgrController.class);		@Resource	private UserService userService;		@RequestMapping(value = "main")	public ModelAndView main(UserCondition userCondition){		ModelMap modelMap = new ModelMap();		Integer totalCount = userService.findUsersCount(userCondition);		List<UserVo> resultList = userService.findUsers(userCondition);		modelMap.addAttribute("resultList", resultList);		modelMap.addAttribute("totalCount", totalCount);		return new ModelAndView("userMgr/main",modelMap);	}	}