package org.jzw.yxbs.service.impl;import java.util.ArrayList;import java.util.List;import javax.annotation.Resource;import org.apache.log4j.Logger;import org.dozer.Mapper;import org.dozer.MappingException;import org.jzw.yxbs.annotation.TrimBlank4String;import org.jzw.yxbs.condition.UserCondition;import org.jzw.yxbs.dao.UserMgrDao;import org.jzw.yxbs.domain.User;import org.jzw.yxbs.service.UserService;import org.jzw.yxbs.vo.UserVo;import org.springframework.stereotype.Service;@Service("userService")public class UserServiceImpl implements UserService {	private static final Logger LOGGER = Logger.getLogger(UserServiceImpl.class);		@Resource	public Mapper dozerMapper;		@Resource	private UserMgrDao userMgrDao;	@TrimBlank4String	@Override	public List<UserVo> findUsers(UserCondition userCondition) {		List<UserVo> resultList = new ArrayList<UserVo>();		try {			List<User> result = userMgrDao.findUsers(userCondition);			for (User user : result) {				resultList.add(dozerMapper.map(user, UserVo.class));			}			return resultList;		} catch (MappingException e) {			LOGGER.error("service错误：", e);			throw new RuntimeException("service错误：",e);		}	}	@TrimBlank4String	@Override	public Integer findUsersCount(UserCondition userCondition) {		try {			return userMgrDao.findUsersCount(userCondition);		} catch (Exception e) {			LOGGER.error("service错误：", e);			throw new RuntimeException("service错误：",e);		}	}	@Override	public List<String> getRoleList() {		try {			return userMgrDao.getRoleList();		} catch (Exception e) {			LOGGER.error("service错误：", e);			throw new RuntimeException("service错误：",e);		}	}}