package org.jzw.yxbs.utils;public class PaginationUtil {	public static Integer getCurrentPage(Integer pageIndex, Integer pageSize, Integer totalCount) {		if (pageIndex == null || pageSize == null || totalCount == null) {			return 0;		}		if (pageSize == 0 || totalCount < 1) {			return 0;		}		if (totalCount < pageSize) {			return 1;		}		return pageIndex + 1;	}	public static Integer getTotalPage(Integer pageIndex, Integer pageSize, Integer totalCount) {		if (pageIndex == null || pageSize == null || totalCount == null) {			return 0;		}		if (pageSize == 0 || totalCount < 1) {			return 0;		}		if (totalCount < pageSize) {			return 1;		}		Integer pages = 0;		if (totalCount % pageSize == 0) {			pages = totalCount / pageSize;		} else {			pages = totalCount / pageSize + 1;		}		return pages;	}}