package org.jzw.yxbs.utils;import org.springframework.security.authentication.encoding.MessageDigestPasswordEncoder;import org.springframework.security.core.context.SecurityContextHolder;import org.springframework.security.core.userdetails.UserDetails;public class UserInfoUtil {	public static String getUserName() {		Object principal = SecurityContextHolder.getContext().getAuthentication().getPrincipal();		String username = "";		if (principal instanceof UserDetails) {			username = ((UserDetails) principal).getUsername();		} else {			username = principal.toString();		}		return username;	}	public static void main(String[] args) {		MessageDigestPasswordEncoder me = new MessageDigestPasswordEncoder("MD5");		String s = me.encodePassword("zzd123", "zzd2");		System.out.println(s);	}}