package org.jzw.yxbs.utils;import org.springframework.security.authentication.encoding.Md5PasswordEncoder;import org.springframework.security.core.context.SecurityContextHolder;import org.springframework.security.core.userdetails.UserDetails;public class UserInfoUtil {		private static Md5PasswordEncoder passwordEncoder;	public void setPasswordEncoder(Md5PasswordEncoder passwordEncoder) {		UserInfoUtil.passwordEncoder = passwordEncoder;	}	public static String getUserName() {		Object principal = SecurityContextHolder.getContext().getAuthentication().getPrincipal();		String username = "";		if (principal instanceof UserDetails) {			username = ((UserDetails) principal).getUsername();		} else {			username = principal.toString();		}		return username;	}		public static String getEncodePasswd(String passwd,String salt){		return passwordEncoder.encodePassword(passwd, salt);	}	}