package org.yxbs.controller;import org.apache.log4j.Logger;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.yxbs.utils.UserInfoUtil;@Controller@RequestMapping("/index")public class IndexController {		private static final Logger LOGGER = Logger.getLogger(IndexController.class);		@RequestMapping(value = "welcome")	public String welcome(){		LOGGER.info("welcome!");		return "welcome";	}		@RequestMapping(value = "login")	public String login(){		LOGGER.info("loginï¼š" + UserInfoUtil.getUserName());		return "login";	}		@RequestMapping(value = "notlogin")	public String notlogin(){		LOGGER.info("notlogin!");		return "notlogin";	}}