package org.yxbs.controller;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;@Controller@RequestMapping("/index")public class IndexController {		@RequestMapping(value = "welcome")	public String welcome(){		return "welcome";	}		@RequestMapping(value = "login")	public String login(){		return "login";	}		@RequestMapping(value = "notlogin")	public String notlogin(){		return "notlogin";	}}