package singletest;import java.io.FileInputStream;import java.io.FileNotFoundException;import java.io.IOException;public class FileTest {	/**	 * @param args	 */	public static void main(String[] args) {		String file1 = "/Users/zhangzhide/Documents/test_source/encode1";		String file2 = "/Users/zhangzhide/Documents/test_source/encode2";		try {			FileInputStream fis1 = new FileInputStream(file1);			FileInputStream fis11 = new FileInputStream(file1);			int temp = 0;			System.out.print("fis11:");			while ((temp = fis11.read())!=-1) {				System.out.print((byte)temp);			}			fis11.close();			System.out.println();			FileInputStream fis2 = new FileInputStream(file2);			byte[] buf1 = new byte[fis1.available()];			byte[] buf2 = new byte[fis2.available()];			fis1.read(buf1);			fis1.close();			fis2.read(buf2);			fis2.close();						System.out.println("buf1.size:"+buf1.length);			System.out.println("buf2.size:"+buf2.length);			System.out.print("before1:");			for (int i=0;i<buf1.length;i++) {				System.out.print(buf1[i]);			}			System.out.println();			System.out.print("before2:");			for (int i=0;i<buf2.length;i++) {				System.out.print(buf2[i]);			}			System.out.println();			String str1 = new String(buf1,"gbk");			String str2 = new String(buf2,"utf8");			System.out.println("value1:"+str1);			System.out.println("value2:"+str2);			System.out.print("after1:");			for (int i =0;i<str1.getBytes().length;i++) {				System.out.print(str1.getBytes()[i]);			}			System.out.println();			System.out.print("after2:");			for (int i =0;i<str2.getBytes().length;i++) {				System.out.print(str2.getBytes()[i]);			}			System.out.println();			byte[] b3 = str1.getBytes("utf8");			System.out.print("modify:");			for (int i =0;i<b3.length;i++) {				System.out.print(b3[i]);			}			System.out.println();			System.out.println(new String(b3));		} catch (FileNotFoundException e) {			e.printStackTrace();		} catch (IOException e) {			e.printStackTrace();		}	}}