package singletest;import java.io.BufferedReader;import java.io.File;import java.io.FileInputStream;import java.io.InputStreamReader;import org.apache.commons.httpclient.HttpClient;import org.apache.commons.httpclient.methods.GetMethod;public class HttpTest {	/**	 * @param args	 */	public static void main(String[] args) {		HttpClient hc = new HttpClient();		File file = new File("/Users/zhangzhide/Documents/test_source/test");		try {			BufferedReader bufRead = new BufferedReader(new InputStreamReader(new FileInputStream(file)));			String str = new String();			while ((str = bufRead.readLine()) != null) {				String postUrl = str.trim()+"/search.htm";				GetMethod getMethod = new GetMethod(postUrl);				int statusCode = hc.executeMethod(getMethod);				byte[] responseBody  = getMethod.getResponseBody();				String result = new String(responseBody,"gbk");				if (result.indexOf("所有宝贝")==-1) {					System.out.println(postUrl+" "+statusCode);				} else {					System.out.println("=");				}			}			bufRead.close();		} catch (Exception e) {			e.printStackTrace();		} 	}}